import React from 'react';
import logo from './logo.svg';
import './HealthcareProvider.css';
import Web3 from 'web3';
import { Table } from 'react-bootstrap';

var web3;
if (typeof window.web3 !== 'undefined') {
  web3 = new Web3(window.web3.currentProvider);
} else {
  // set the provider you want from Web3.providers
  web3 = new Web3(new Web3.providers.HttpProvider("http://localhost:8545"));
}

const inputStyle = {
  borderRadius:5,
  fontSize:15,
  borderTop:0,
  borderLeft:0,
  borderRight:0,
  height:30,
  marginRight:10
};
const buttonStyle = {
  borderRadius:5,
  fontSize:15,
  borderTop:0,
  borderLeft:0,
  borderRight:0,
  height:40,
  width: 90,
  marginLeft: 180,
  color:"white",
  backgroundColor:"coral"
};
const buttonStyle2 = {
  borderRadius:5,
  fontSize:15,
  borderTop:0,
  borderLeft:0,
  borderRight:0,
  height:40,
  width: 90,
  marginLeft: 10,
  color:"white",
  backgroundColor:"coral"
};
const buttonStyle3 = {
  borderRadius:5,
  fontSize:15,
  borderTop:0,
  borderLeft:0,
  borderRight:0,
  height:30,
  width: 30,
  marginLeft: 3,
  color:"white",
  backgroundColor:"blue"
};
var CheckID = React.createClass({
  getInitialState: function(){
    return {BSN:"", Age:"", Gender:"", Issuer:"",address:''};
  },
  getUserInfo: function(){
    var tempRes;
    var userAddress = this.state.address;
    return new Promise(function(resolve, reject) {
      myContract.readID(userAddress,{from: accounts[0]},function(err, res) {
       if (err) {
         console.log(err);
       } else {
       tempRes = res;
       resolve(tempRes);
       }
      })
   })
  },
  handleInputAddress: function(adr){
    this.setState({address:adr.target.value});
  },
  checkInfo: async function(){
    console.log(this.state.address);
    var result = await this.getUserInfo();
    this.setState({BSN:String(result[1]), Age:String(result[3]), Gender:String(result[2]), Issuer:String(result[4])});
  },
  render: function(){
    return(
    <div>
      <nobr>
        <input style={inputStyle} placeholder = "Patient Address" size = "45" type="text" onChange = {this.handleInputAddress} value = {this.state.address}>
        </input>
        <button style={buttonStyle2} onClick = {this.checkInfo}>Check</button>
      </nobr>
      <table>
        <tr>
          <th></th>
          <th></th>
        </tr>
        <tr>
          <td>BSN</td>
          <td>{this.state.BSN}</td>
        </tr>
        <tr>
          <td>Age</td>
          <td>{this.state.Age}</td>
        </tr>
        <tr>
          <td>Gender</td>
         <td>{this.state.Gender}</td>
        </tr>
        <tr>
          <td>Issued by</td>
        <td>{this.state.Issuer}</td>
        </tr>
      </table>
    </div>
    );
  }
});
function HealthcareProvider() {
  return (
    <div className="">
      <header className="HealthcareProvider-header">
        <img src={logo} className="HealthcareProvider-logo" alt="logo" />
        <p>
          Edit <code>src/HealthcareProvider.js</code> and save to reload.
        </p>
        <a
          className="HealthcareProvider-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default HealthcareProvider;
